import React from 'react'
import { useSelector } from 'react-redux'
import { Navigate } from 'react-router-dom'

const ProtectComponent = ({role, children}) => {
    const user = useSelector( state => state.userTokener )
    const admin = useSelector( state => state.adminTokener )
    console.log("user : ",user)
    if(user)
    {
        if(user._userId && user.role === role)
        return children
        else
        {
        console.log("home redirected")
        return <Navigate to='/home' replace/>
        }
    }

    if(admin && admin._adminId && admin.role === role)
    {
        if(admin._adminId && admin.role === role)
        return children
        else
        <Navigate to='/admin/home' replace/>
    }
}

export default ProtectComponent
