import axios from "axios"
import { SET_SHOW, SET_BOOKING_STATUS } from "./actionTypes"
const setShow = (dates) => {
    return {
        type : SET_SHOW,
        payload : dates
    }
}
const setBooking = (message) => {
    return {
        type : SET_BOOKING_STATUS,
        payload : message
    }
}
const viewShow = (movieId) => {
    return(dispatch) => {
        axios.get(`http://localhost:3040/shows/${movieId}`)
        .then((show) => {
            console.log("show data : ",show.data.showAvailableDates)
            dispatch(setShow(show.data.sho))
        })
        .catch((err) => console.log(err))
    }
}
const bookShow = (bookingDetails) => {
    return(dispatch) =>
    { 
    axios.post(`http://localhost:3040/shows/book`,bookingDetails)
    .then( book => {
        dispatch(setBooking(book.data))
    })
    .catch(err => {
        if(err.response.status === 400)
        console.log("error : ",err.response.data.errorMessage)
    })}
}
export {
    viewShow,
    bookShow
}
