import { SET_LOGIN, SET_LOGOUT, SET_SIGNUP, SET_SIGNOUT, TOGGLE_SIGNUP, SET_ADMIN_LOGIN, SET_ADMIN_LOGOUT, SET_TOKEN, SET_RETRIEVE_TOKEN } from "./authTypes"
import axios from 'axios'

const setLogin = () => {
    return {
        type : SET_LOGIN
    }   
}
const setLogout = () => {
    return {
       type : SET_LOGOUT
    }  
}
