}
const setAdminLogin = () => {
    return {
        type : SET_ADMIN_LOGIN
    }
} 
const setAdminLogout = () => {
    return {
        type : SET_ADMIN_LOGOUT
    }
} 
const setToken = (token) => {
    return {
        type : SET_TOKEN,
        token : token.data
    }
}
const setRetrieveToken = (token) => {
    return {
        type : SET_RETRIEVE_TOKEN,
        token : token.data
    }
}
const storeUserToken = (user, type = 'login') => {
    console.log("hello")
    return(dispatch) => {
        axios.post(`http://localhost:3040/users/${type}`,user)
        .then( token => {
            if(type === 'login')
            {
            localStorage.setItem("usersToken", token.data.accessToken)
            dispatch(setToken(token))
            }
        })
        .catch( err => console.log(err) )
    }
}
const retrieveToken = () => {
    return(dispatch, getState) => {
        const token = getState().token.tokenState
        if(token){
            dispatch(setRetrieveToken(token))
        } 
    }
}
export {
    setLogin,
    setLogout,
    setSignUp,
    setSignOut,
    toggleSignup,
    setAdminLogin,
    setAdminLogout,
    storeUserToken,
    retrieveToken
}
