import { SET_USER_TOKEN, SET_USER_RETRIEVE_TOKEN, DELETE_USER_TOKEN, SET_PROFILE, SET_USER_LOGIN_SUCCESS } from "./ActionTypes"
import axios from "axios"

const setUserToken = (token) => {
    return {
        type : SET_USER_TOKEN,
        token
    }
}
const setUserRetrieveToken = (token) => {
    return {
        type : SET_USER_RETRIEVE_TOKEN,
        token
    }
}
const deleteUserToken = () => {
    return {
        type : DELETE_USER_TOKEN
    }
}
const setProfile = (user) => {
    return {
        type : SET_PROFILE,
        payload : user
    }
}
const setUserLoginSuccess = (message) => {
    return {
        type : SET_USER_LOGIN_SUCCESS,
        payload : message
    }
}
const setUserLoginError = (message) => {
    return {
        type : SET_USER_LOGIN_ERROR,
        payload : message
    }
}
const storeUserToken = (user, type = 'login') => {
    return(dispatch) => {
        axios.post(`http://localhost:3040/users/${type}`,user)
        .then( token => {
            if(type === 'login')
            {
            sessionStorage.setItem("usersToken", token.data.accessToken)
            dispatch(setUserToken(token.data.accessToken))
            dispatch(setUserLoginSuccess('successfully logged in'))
            }
        })
        .catch( err => {
            if(err.response.status === 400)
            {
                console.log("actions : ",err.response.data.errorMessage)
                dispatch(setUserLoginError(err.response.data.errorMessage)) 
            }
        })
    }
}
const retrieveUserToken = () => {

    return(dispatch, getState) => {
        const token = getState().userTokener.userToken
        if(token){
            dispatch(setUserRetrieveToken(token))
        } 
    }
}
const viewProfile = (userId) => {
    return(dispatch) => {
        axios.get(`http://localhost:3040/users/my-profile/${userId}`)
        .then((user) => {
            dispatch(setProfile(user.data.user))
        })
        .catch( err => console.log(err) )
    }
}
const updateProfile = (userDetails,userId) => {
    return (dispatch) => {
        axios.put(`http://localhost:3040/users/${userId}`,userDetails)
        .then(() => { 
            dispatch(setProfile(userDetails))
        })
        .catch( err => console.log("error : ",err))
    }
}

export {
    storeUserToken,
    retrieveUserToken,
    deleteUserToken,
    viewProfile,
    updateProfile
}
