let movieInitialState = {
    movies : []
}

const movieReducer = ( state = movieInitialState, action ) => {
    switch(action.type){
        case 'SET_MOVIE' : return {
            ...movieInitialState,
            movies : action.payload
        }
    }
}

export default movieReducer
