import { Box, Button, Card, CardContent, CardMedia, Checkbox, Grid, Typography } from '@material-ui/core'
import { makeStyles } from "@material-ui/styles";
import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { bookShow, viewSeats } from '../redux/showActions';


const label = { inputProps: { 'aria-label': 'Checkbox demo' } }
const useStyles = makeStyles({
  root: {
    margin: 'auto',
    padding: 15,
    marginBottom: 20,
  },
  card: {
        margin : 'auto',
        maxWidth : 350,
        maxHeight : 500,
        padding : 15,
        marginBottom : 20,
    }
})

const SeatDisplay = ({data}) => {
  const dispatch = useDispatch()
  const classes = useStyles()
  const userId = useSelector( state => state.userTokener._userId )
  const seat = useSelector( state => state.seat.seats )
  const [seats, setSeats] = useState([])
  const selectSeats = (event) => {
    if (!seats.includes(event.target.value))
      setSeats(prev => [...prev, event.target.value])
    else 
    {
      let filteredSeats = seats.filter(seat => seat !== event.target.value)
      setSeats(filteredSeats)
    }
  }

const bookHandler = () => {
    dispatch(bookShow({ movieId : data.selectedMovieId, showDate : data.selectedDate, userId, seats }))
}
  return (<>



    {seats && (<><Typography>{seats.toString()}</Typography></>) }
    {/* <Checkbox {...label} disabled checked /> */}
  </>)
}

export default SeatDisplay
