import { Grid, ToggleButtonGroup } from "@mui/material";
import { Box } from "@mui/system";
import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { viewShow } from "../redux/showTypes";
import Date from '../movie/Date'
import { ToggleButton } from "react-bootstrap";
const Shows = () => {
  const dispatch = useDispatch()
  const { movieId } = useParams()
  const dates = useSelector(state => (state.show.shows.showAvailableDates))
  console.log("dates array : ",dates)
  const [toggle, setToggle] = useState('');
  const tabChange = (event, newTab) => {
    setToggle(newTab);
  };
  useEffect(() => {
    dispatch(viewShow(movieId))
  }, [movieId])
  return (<>
    <Box p={10} pt={10} >
      <Grid container spacing={0.5}>
        Shows Page
        <ToggleButtonGroup value={toggle} onChange={tabChange} exclusive style={{ marginLeft: 400, marginTop: 70 }}>
          {
          dates && dates.map((date) => (
              <ToggleButton value={toggle} >{date.showDate.substring(0, 10)}</ToggleButton>))
          }
        </ToggleButtonGroup>
      </Grid>
    </Box>
  </>)
}

export default Shows
