import React, { useState } from 'react'
import { Button, TextField, Typography } from '@mui/material'
import { Box } from '@mui/system'
import { useStyles } from '../../styles/styles'
import { useDispatch } from 'react-redux'
import { addMovies } from '../redux/MovieActions'
import { useNavigate } from 'react-router-dom'
import { useForm } from 'react-hook-form'
import ReactJsAlert from "reactjs-alert"
import '../../styles/Style.css'

 const AdminMovies = () => {
  const classes = useStyles()
  const dispatch = useDispatch()
  const navigate = useNavigate()
  const { register, handleSubmit, formState : {errors}, watch , reset} = useForm({
    mode : 'onChange'
  })
  const [ movieDetails, setMovieDetails ] = useState({
    movieImageUrl : '', 
    movieVideoUrl : '', 
    movieName : '', 
    ticketCost : '', 
    description : '',
    actorName : '',
    directorName : '',
    startBookingDate : new Date(''),
    endBookingDate : new Date('')
  })
  const detailsHandler = (event) => {
    setMovieDetails({
      ...movieDetails,
      [event.target.name] : event.target.value
    })
  }
  const submitHandler = (event) => {
    event.preventDefault()
    dispatch(addMovies(movieDetails))
    navigate('/admin/home')
  }
    return(<>
      <form  onSubmit={ handleSubmit(submitHandler) }>
        <Box className = {classes.loginForm}>
          <Typography padding={1} variant='h4' textAlign="center">
           Add Movie
          </Typography>
          <TextField {...register('movieImageUrl',{ required : 'Image url required', 
            pattern : {
              value : /^https?:\/\/.+\.(jpg|jpeg|png|webp|avif|gif|svg)$/,
              message : 'Invalid image url'
            }})} type={'text'} placeholder='Image Url' margin='normal' required/>
          <TextField {...register('movieVideoUrl',{ required : 'Video url required', 
            pattern : {
              value : /^(http\:\/\/)?(youtube\.com|youtu\.be)+$/,
              message : 'Invalid video url'
            }})} type={'text'} placeholder='Video Url' margin='normal' required/>
          <TextField type={'text'} {...register('movieName',{ required : 'Movie name required', 
            pattern : {
              value : /^[a-zA-Z0-9 ]+$/,
              message : 'Movie name should contain only alphabets and digits for sequel'
            }})} placeholder='Movie Name' margin='normal' required/>
          <TextField type={'number'} {...register('ticketCost',{ required : 'Ticket cost required', 
            min : {
              value : 190,
              message : 'Ticket price should be atleast 190rs'
            },
            max : {
              value : 500,
              message : 'Ticket price should not exceed 500rs'
            }
            })} placeholder='Ticket Cost' margin='normal' required/>
          <TextField type={'text'} {...register('description',{ required : 'Movie description required'
           })} placeholder='Description' margin='normal' required/>
          <TextField type={'text'} {...register('actorName',{ required : 'Actor name required', 
            pattern : {
              value : /^[a-zA-Z ]+$/,
              message : 'Movie name should contain only alphabets and digits for sequel'
            }})} placeholder='Actor Name' margin='normal' required/>
          <TextField type={'text'} name='directorName' value={movieDetails.directorName} onChange={detailsHandler} placeholder='Director Name' margin='normal' required/>
          <TextField type={'date'} name='startBookingDate' value={movieDetails.startBookingDate} onChange={detailsHandler}  margin='normal' required/>
          <TextField type={'date'} name='endBookingDate' value={movieDetails.endBookingDate} onChange={detailsHandler} margin='normal' required/>

          <Button type='submit' variant='contained' color='warning' style={{margin : '5% 0'}}>Add</Button>
        </Box>
      </form>
    </>)
 }

 export default AdminMovies
