import jwtdecode from 'jwt-decode'

const initialState = {
    login : false,
    signup : false,
    user : true,
    adminLogin : false
}
const tokenState = {
    token : localStorage.getItem("usersToken"),
    userName : '',
    userEmail : '',
    userPassword : '',
    userConfirmPassword : '',
    userContact : '',
    _id : ''
}

const authReducer = (state = initialState, action) => {
    switch(action.type){
        case 'SET_LOGIN' : return {
            ...state,
            login : true
        }
        case 'SET_LOGOUT' : return {
            ...state,
            login : false
        }
        case 'SET_SIGNUP' : return {
            ...state,
            signup : true
        }
        case 'SET_SIGNOUT' : return {
            ...state,
            signup : false
        }
        case 'TOGGLE_SIGNUP' : return {
            ...state,
            signup : ! state.signup
        }
        case 'SET_ADMIN_LOGIN' : return {
            ...state,
            adminLogin : true
        }
        case 'SET_ADMIN_LOGOUT' : return {
            ...state,
            adminLogin : false
        }
        default : return state
    }
}
const tokenReducer = (state = tokenState, action) => {
    console.log("token .....")
