import React, { useState,useEffect } from 'react'
import { AppBar, Button, Box, Toolbar, Typography, Tabs, Tab, } from '@mui/material'
import { Link, useLocation } from 'react-router-dom'
import { useStyles } from '../styles/styles'
import { appBar, headerMenu, } from '../styles/styles'
import { useDispatch, useSelector } from 'react-redux'
import { setLogout } from './redux/authActions'

const AdminHeader = () => {
    const classes = useStyles()
    const location = useLocation()
    const login = useSelector(state => state.login)
    const dispatch = useDispatch()
    const [selectTab, setSelectTab] = useState(0)
    const logoutHandler = () => {
        dispatch(setLogout())
    }
    const loginHandler = () => {
        dispatch(setSignOut())
    }
    useEffect( () => {
        if(location.pathname === "/admin" || location.pathname === '/admin/home')
        {
            setSelectTab(0)
        }
        else if(location.pathname === "/admin/movies")
        {
            setSelectTab(1)
        }
        else if(location.pathname === "/admin/bookings")
        {
            setSelectTab(2)
        }
    },[])
    return (<>
        <AppBar position='sticky' style={appBar}>
            <Toolbar>
                <Typography variant='h4' margin="0 5%">TicketNew</Typography>
                <Tabs value={selectTab} onChange={ (event, value) => setSelectTab(value) } >

                    <Tab LinkComponent={Link} to='/admin/home' label="Home" style={headerMenu} />
                    <Tab LinkComponent={Link} to='/admin/movies' label="Upload Movies" style={headerMenu} />
                    <Tab LinkComponent={Link} to='/admin/bookings' label="Bookings" style={headerMenu} />

                </Tabs>

                <Box display="flex" marginLeft="auto">
                    {
                        !login && <>
                            <Button onClick={loginHandler} LinkComponent={Link} to='/auth' variant='outlined' sx={{ margin: 1, borderRadius: 3 }} color='primary'>Login</Button>
                            <Button onClick={signupHandler} LinkComponent={Link} to='/auth' variant='outlined' sx={{ margin: 1, borderRadius: 3 }} color='warning'>Signup</Button>
                        </>
                    }
                    {
                        login && <Button onClick={logoutHandler} LinkComponent={Link} to='/home' variant='outlined' sx={{ margin: 1, borderRadius: 3 }} color='warning'>Logout</Button>
                    }
                </Box>
            </Toolbar>
        </AppBar>
        <p> login = {login ? "true" : "false"} </p>
        <p> signup = {signup ? "true" : "false"} </p>
    </>)
}

export default AdminHeader
