import React, { useState } from 'react'
import { Button, TextField, Typography } from '@mui/material'
import { Box } from '@mui/system'
import { useStyles } from '../../styles/styles'
import { useDispatch } from 'react-redux'
import { addMovies } from '../redux/MovieActions'
import { useNavigate } from 'react-router-dom'
import { useForm } from 'react-hook-form'
import ReactJsAlert from "reactjs-alert"
import '../../styles/Style.css'

 const AdminMovies = () => {
  const classes = useStyles()
  const dispatch = useDispatch()
  const navigate = useNavigate()
  const { register, handleSubmit, formState : {errors}, watch , reset} = useForm({
    mode : 'onChange'
  })

  const submitHandler = (movieData) => {
    dispatch(addMovies(movieData))
    navigate('/admin/home')
  }
    return(<>
      <form onSubmit={ handleSubmit(submitHandler) }>
        <Box className = {classes.loginForm}>
          <Typography padding={1} variant='h4' textAlign="center">
           Add Movie
          </Typography>
          <TextField {...register('movieImageUrl',{ required : 'Image url required',
            pattern : {
              value : /^https?:\/\/.+\.(jpg|jpeg|png|webp|avif|gif|svg)$/,
              message : 'Invalid image url'
            }
          })} type={'text'}  placeholder='Image Url' margin='normal'/>
          { errors.movieImageUrl && <small className='credential-error'>{errors.movieImageUrl.message}</small>}
          <TextField {...register('movieVideoUrl',{ required : 'Video url required',
            pattern : {
              value : /^(http\:\/\/)?(youtube\.com|youtu\.be)+$/,
              message : 'Invalid video url'
            }
          })} type={'text'} placeholder='Video Url' margin='normal'/>
          { errors.movieVideoUrl && <small className='credential-error'>{errors.movieVideoUrl.message}</small>}
          <TextField {...register('movieName',{ required : 'Movie name required',
            pattern : {
              value : /^[a-zA-Z0-9 ]+$/,
              message : 'Movie name should contain only alphabets and digits if any sequel'
            }
          })} type={'text'} placeholder='Movie Name' margin='normal'/>
          { errors.userContact && <small className='credential-error'>{errors.userContact.message}</small>}
          <TextField {...register('ticketCost',{ required : 'Ticket cost required',
            min : {
              value : 190,
              message : 'Ticket price should be atleast 190rs'
            },
            max : {
              value : 'Ticket price should not exceed 500rs'
            }
          })} type={'number'} placeholder='Ticket Cost' margin='normal'/>
          <TextField {...register('description',{ required : 'Movie description required' })} 
            type={'text'} placeholder='Description' margin='normal'/>
          <TextField {...register('actorName',{ required : 'Actor name required',
            pattern : {
              value : /^[a-zA-Z ]+$/,
              message : 'Actor name should contain only alphabets'
            }
          })} type={'text'} placeholder='Actor Name' margin='normal'/>
          <TextField {...register('directorName',{ required : 'Director name required',
            pattern : {
              value : /^[a-zA-Z ]+$/,
              message : 'Director name should contain only alphabets'
            }
          })} type={'text'} placeholder='Director Name' margin='normal'/>
          <TextField {...register('startBookingDate',{ required : 'Release date required'
          })} type={'date'} margin='normal' required/>
          <TextField {...register('endBookingDate',{ required : 'Out date required'
          })} type={'date'} margin='normal' required/>

          <Button type='submit' variant='contained' color='warning' style={{margin : '5% 0'}}>Add</Button>
        </Box>
      </form>
    </>)
 }

 export default AdminMovies
