import React, { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { Navigate } from 'react-router-dom'
import { retrieveAdminToken } from './redux/AdminActions'
import { retrieveUserToken } from './redux/UserActions'

const ProtectComponent = ({role, children}) => {
    const user = useSelector( state => state.user )
    const admin = useSelector( state => state.admin )

    if(user._userId && user.userRole === role)
    return children

    if(admin._adminId && admin.adminRole === role)
    return children

    if(role === 'admin')
    return <Navigate to='/admin/login' replace/>

    else if(role === 'user')
    {
        console.log("re render to auth : ",role)
        return <Navigate to='/auth' replace/>
    }

}

export default ProtectComponent
