import jwtdecode from 'jwt-decode'

const initialState = {
    login : false,
    signup : false,
    user : true,
    adminLogin : false
}
const tokenState = {
    tooken : localStorage.getItem("usersToken"),
    userName : '',
    _id : ''
}
// const userInitialState = {
//     userName : '',
//     userEmail : '',
//     userPassword : '',
//     userConfirmPassword : ''
// }
const authReducer = (state = initialState, action) => {
    switch(action.type){
        case 'SET_SIGNUP' : return {
            ...state,
            signup : true
        }
        case 'SET_SIGNOUT' : return {
            ...state,
            signup : false
        }
        case 'TOGGLE_SIGNUP' : return {
            ...state,
            signup : ! state.signup
        }
        case 'SET_ADMIN_LOGIN' : return {
            ...state,
            adminLogin : true
        }
        case 'SET_ADMIN_LOGOUT' : return {
            ...state,
            adminLogin : false
        }
        default : return state
    }
}
const tokenReducer = (state = tokenState, action) => {
    switch(action.type){
        case 'SET_USER_TOKEN' : 
        case 'SET_USER_RETRIEVE_TOKEN' :
        const user = jwtdecode(action.token)
        console.log("after refresh : ",user)
        return {
            ...tokenState,
            token : action.token,
            userName : user.userName,
            _id : user.id
        }
        case 'DELETE_USER_TOKEN' : localStorage.removeItem("usersToken")
        return {
            ...tokenState,
            tokeoken : '',
            userName : '',
            _id : ''
        }
        default : return state
    }
}

export {
    authReducer,
    tokenReducer
}
