import axios from "axios"
import { SET_BOOK } from "./actionTypes"
const setBook = (book) => {
    return {
        type : SET_BOOK,
        payload : book
    }
}

const viewBook = (userId) => {
    return(dispatch) => {
        axios.get(`http://localhost:3040/shows/${userId}`)
        .then(book => {
            console.log("bookings : ",book)
            dispatch(book)
        })
    }
}

export {
    viewBook
}
