class ProtectComponent extends Component{
    render(){
        const role = this.props.role
        const user = jwtDecode(this.props.user.userToken)
        const admin = jwtDecode(this.props.admin.adminToken)
        console.log("role passes : ",role)
        if(user.id && user.role === role)
        return this.props.children

        if(admin.id && admin.role === role)
        return this.props.children

        if(role === 'admin')
        return <Navigate to='/admin' replace/>
        else if(role === 'user')
        return <Navigate to='/auth' replace/>
    }
}

const mapUserStateToProps = state => ({
    user : state.user,
    admin : state.admin
})



export default connect(mapUserStateToProps)(ProtectComponent)
