import jwtdecode from 'jwt-decode'

const initialState = {
    profile : ''
}
const tokenState = {
    userToken : sessionStorage.getItem("usersToken"),
    _userId : ''
}
const userLoginInitialState = {
    message : '',
    loginSuccess : false,
    loginFailed : false
}
const userReducer = (state = initialState, action) => {
    switch(action.type){
        case 'SET_PROFILE' : return {
            ...state,
            profile : action.payload
        }
        default : return state
    }
}
const tokenUserReducer = (state = tokenState, action) => {
    switch(action.type){
        case 'SET_USER_TOKEN' : 
        case 'SET_USER_RETRIEVE_TOKEN' :
        const user = jwtdecode(action.token)
        return {
            ...tokenState,
            userToken : action.token,
            _userId : user.id
        }
        case 'DELETE_USER_TOKEN' : sessionStorage.removeItem("usersToken")
        return {
            userToken : '',
            _userId : ''
        }
        default : return state
    }
}
const userErrorReducer = (state = userErrorInitialState, action) => {
    console.log("action err : ",action.payload)
    switch(action.type){
        case 'SET_USER_ERRORS' : return {
            ...userLoginInitialState,
            errors : action.payload
        }
        default : return state
    }
}

export {
    userReducer,
    tokenUserReducer,
    userErrorReducer
}
