import React from 'react'
import { useSelector } from 'react-redux'
import { Navigate } from 'react-router-dom'

const ProtectComponent = ({role, children}) => {
    const user = useSelector( state => state.userTokener )
    const admin = useSelector( state => state.adminTokener )

    if(user)
    {
        if(user._userId && user.role === role)
        return children
        else
        <Navigate to='/home' replace></Navigate>
    }

    if(admin && admin._adminId && admin.role === role)
    return children
}

export default ProtectComponent
