import axios from "axios"
import { BookBase } from "../api/BaseUrl"
import axiosInstance from "../api/Interceptors"
import { SET_BOOK } from "./ActionTypes"

const setBook = (book) => {
    return {
        type : SET_BOOK,
        payload : book
    }
}
const setBookings = (bookings) =>  {
    return {
    type : 'SET_BOOKINGS',
    payload : bookings
    }
}
const viewBook = (userId) => {
    return(dispatch) => {
        axiosInstance({
            url: `bookings/my-profile/${userId}`,
            method: "get"
        })
        axios.get(`${BookBase}/${userId}`)
        .then(book => {
            dispatch(setBook(book.data.bookings))
        })
    }
}
const viewAllBookings = () => {
    return(dispatch) => {
        axios.get(`${BookBase}`)
        .then( bookings => {
            dispatch(setBookings(bookings.data.bookings))
        })
        .catch((err) => console.log("err : ",err))
    }
}


export {
    viewBook,
    viewAllBookings
}
