import { SET_MOVIE, VIEW_MOVIE } from "./movieTypes"
import axios from 'axios'

const setMovie = (movies) => {
    return {
        type : SET_MOVIE,
        payload : movies
    }
}
const setSingleMovie = (movie) => {
    return {
        type : SET_MOVIE,
        payload : movie
    }
}

const addMovies = (movieDetails) => {
    return (dispatch) => {
        axios.post('http://localhost:3040/movies',movieDetails)
        .then(movie => { 
            dispatch(setMovie(movie.data.movie))
        })
        .catch( err => console.log("error : ",err))
    }
}
const viewMovie = () => {
    return(dispatch) => {
        axios.get(`http://localhost:3040/`)
    }
}

export {
    addMovies,
    viewMovie
}
