import { Button, TextField, Typography } from '@mui/material'
import { Box } from '@mui/system'
import React, { useState } from 'react'
import { Link, useNavigate } from 'react-router-dom'
import { useStyles } from '../../styles/styles'
import { useSelector ,useDispatch } from 'react-redux'
import { setSignOut, toggleSignup } from '../redux/SignupActions'
import { storeUserToken } from '../redux/UserActions'
import { useForm } from 'react-hook-form'

 let inputFieldStyles = {
  width : '100%'
 }

const Login = () => {
    const navigate = useNavigate()
    const dispatch = useDispatch()
    const { register, handleSubmit, formState : {errors}, reset, watch} = useForm()
    const signup = useSelector( state => state.signUp.signup )

    const [ userCredentials, setUserCredentials] = useState({
      userName : '',
      userEmail : '',
      userPassword : '',
      userConfirmPassword : '',
      userContact : ''
    })
    const classes = useStyles()
    const changeCredentialHandler = (event) => {
      setUserCredentials((prevState) => ({
        ...prevState,
        [event.target.name] : event.target.value
      }))
    }
    const signupHandler = () => {
      dispatch(toggleSignup())
    }
    const submitHandler = (data) => {

      console.log("data : ",data)
      if(signup)
      {
        dispatch(storeUserToken(userCredentials, 'signup'))  
        dispatch(setSignOut())                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             
        navigate('/auth')
      }
      else{
        dispatch(storeUserToken(userCredentials))
        navigate('/home',{replace : true})
      }
    }
    const confirmPassword = watch('userConfirmPassword')
    console.log("errors : ",errors)
    return(<>
      <form  onSubmit={ handleSubmit(submitHandler) }>
        <Box className = {classes.loginForm}>
          <Typography padding={1} variant='h4' textAlign="center">
            { signup ? "Signup" : "Login" }
          </Typography>
          { signup && <TextField {...register('userName',{ required : 'Username required', 
            pattern : {
              value : /^[a-zA-Z ]+$/,
              message : 'Username should contain only alphabets'
            },
            minLength : {
            value : 3,
            message : 'Username should contain atleast 3 letters'
          }})} type={'text'} value={userCredentials.userName} style={inputFieldStyles} onChange={changeCredentialHandler} placeholder='Username' margin='normal' />  }
          { errors.userName && <small>{errors.userName.message}</small>}
          <TextField type={'email'} {...register('userEmail',{ required : 'Email required',
           pattern : {
             value : /^([a-z]+[\.-\d]*)@([a-z-]+)\.([a-z\-]{2,8})(\.[a-z]{2,8})?$/,
             message : 'Invalid email format'
           }
          })} value={userCredentials.userEmail} style={inputFieldStyles} onChange={changeCredentialHandler} placeholder='EmailID' margin='normal' />
          { errors.userEmail && <small>{errors.userEmail.message}</small>}
          <TextField type={'password'} {...register('userPassword',{ required : 'Password required',
           pattern : {
            value : /^[a-zA-Z0-9]{8,20}$/,
            message : 'Password should contain atleast 8 characters'
           }
          })} value={userCredentials.userPassword} style={inputFieldStyles} onChange={changeCredentialHandler} placeholder='Password' margin='normal' />
          { errors.userPassword && <small>{errors.userPassword.message}</small>}
          {
            signup && <><TextField type={'password'} {...register('userConfirmPassword',{ required : 'Password required',
            validate : confirm => confirm === confirmPassword
           })}  value={userCredentials.userConfirmPassword} style={inputFieldStyles} onChange={changeCredentialHandler} placeholder='Confirm Password' margin='normal' />
            <TextField type={'text'} {...register('userContact',{ required : 'Contact required'})} value={userCredentials.userContact} style={inputFieldStyles} onChange={changeCredentialHandler} placeholder='Contact Number' margin='normal' />
            { errors.userContact && <small>Contact required</small>}
            </>
          }
          <Button type='submit' variant='contained' color='warning' style={{margin : '5% 0'}}>{ signup ? "Signup" : "Login" }</Button>
          { !signup && <Typography>Dont't have an account ?</Typography> }
          <Link onClick={signupHandler} to='/auth'>{ !signup && "Signup"}</Link>
        </Box>
      </form>
    </>)
 }

 export default Login
