import { Button, TextField, Typography } from '@mui/material'
import { Box } from '@mui/system'
import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom'
import { useStyles } from '../../styles/styles'
import { useSelector ,useDispatch } from 'react-redux'
import { storeAdminToken } from '../redux/AdminActions'
import { deleteUserToken, storeUserToken } from '../redux/UserActions'


const AdminLogin = () => {
    const navigate = useNavigate()
    const dispatch = useDispatch()
    const admin = useSelector( state => state.userTokener )
    const [ adminCredentials, setAdminCredentials] = useState({
      userEmail : '',
      userPassword : ''
    })
    const classes = useStyles()
    const changeCredentialHandler = (event) => {
        setAdminCredentials((prevState) => ({
        ...prevState,
        [event.target.name] : event.target.value
      }))
    }
    const submitHandler = (event) => {
      event.preventDefault()
      dispatch(storeUserToken(adminCredentials))
      if(admin._userId && admin.role == 'admin')
      navigate('/admin/home',{replace:true})
      else
      {
        sess
        navigate('/',{replace : true})
      }
    }
    return(<>
      <form  onSubmit={ submitHandler }>
        <Box className = {classes.loginForm}>
          <Typography padding={1} variant='h4' textAlign="center">
           Admin Login
          </Typography>
          <TextField type={'email'} name='userEmail' value={adminCredentials.userEmail} onChange={changeCredentialHandler} placeholder='EmailID' margin='normal' required/>
          <TextField type={'password'} name='userPassword' value={adminCredentials.userPassword} onChange={changeCredentialHandler} placeholder='Password' margin='normal' required/>
          <Button type='submit' variant='contained' color='warning' style={{margin : '5% 0'}}>Login</Button>
        </Box>
      </form>

    </>)
 }

 export default AdminLogin
