import jwtdecode from 'jwt-decode'

const initialState = {
    profile : ''
}
const tokenState = {
    userToken : sessionStorage.getItem("usersToken"),
    _userId : ''
}
const userLoginSuccesState = {
    message : '',
    loginSuccess : false
}
const userLoginFailState = {
    message : '',
    loginFailed : false
}
const userReducer = (state = initialState, action) => {
    switch(action.type){
        case 'SET_PROFILE' : return {
            ...state,
            profile : action.payload
        }
        default : return state
    }
}
const tokenUserReducer = (state = tokenState, action) => {
    switch(action.type){
        case 'SET_USER_TOKEN' : 
        case 'SET_USER_RETRIEVE_TOKEN' :
        const user = jwtdecode(action.token)
        return {
            ...tokenState,
            userToken : action.token,
            _userId : user.id
        }
        case 'DELETE_USER_TOKEN' : sessionStorage.removeItem("usersToken")
        return {
            userToken : '',
            _userId : ''
        }
        default : return state
    }
}
const userSuccessReducer = (state = userLoginSuccesState, action) => {
    console.log("action err : ",action.payload)
    switch(action.type){
        case 'SET_USER_LOGIN_SUCCESS' : return {
            ...userLoginSuccesState,
            message : action.payload,
            loginSuccess : true
        }
        default : return state
    }
}
const userErrorReducer = (state = userLoginFailState, action) => {
    switch(action.type){
        case 'SET_USER_LOGIN_ERROR' : return {
            ...userLoginFailState,
            message : action.payload,
            loginFailed : true
        }
    }
}
export {
    userReducer,
    tokenUserReducer,
    userSuccessReducer
}
