const bookInitialState = {
    bookings : '',
    allBookings : []
}

const bookReducer = (state = bookInitialState, action) => {
    switch(action.type){
        case 'SET_BOOK' : return {
            ...state,
            bookings : action.payload
        }
        case 'SET_BOOKINGS' : return {
            ...state,
            allBookings : action.payload
        }
        default : return state
    }
}


export {
    bookReducer
}
