import axios from "axios"
import { SET_BOOK } from "./ActionTypes"
const setBook = (book) => {
    return {
        type : SET_BOOK,
        payload : book
    }
}
const setBookings = (bookings) =>  {
    return {
    type : 'SET_BOOKINGS',
    payload : bookings
    }
}
const viewBook = (userId) => {
    return(dispatch) => {
        axios.get(`http://localhost:3040/bookings/${userId}`)
        .then(book => {
            dispatch(setBook(book.data.bookings))
        })
    }
}
const viewAllBookings = () => {
    return(dispatch) => {
        axios.get(`http://localhost:3040/bookings`)
        .then( bookings => {
            dispatch(setBookings(bookings.data.bookings))
        })
        .catch((err) => console.log("err : ",err))
    }
}


export {
    viewBook,
    viewAllBookings
}
