import axios from "axios"
import { SET_SHOW, SET_SEATS } from "./actionTypes"
const setShow = (dates) => {
    return {
        type : SET_SHOW,
        payload : dates
    }
}
const setBooking = () => {
    return {
        type : SET_BOOKING
    }
}
const viewShow = (movieId) => {
    return(dispatch) => {
        axios.get(`http://localhost:3040/shows/${movieId}`)
        .then((show) => {
            // console.log("shows sheduled : ",show.data)
            dispatch(setShow(show.data))
        })
        .catch((err) => console.log(err))
    }
}
const bookShow = (bookingDetails) => {
    console.log("booking  : ",bookingDetails)
    axios.post(`http://localhost:3040/shows/book`,bookingDetails)
    .then( data => console.log("data : ",data))
    .catch(err => {
        if(err.response.status === 400)
        console.log("error : ",err.response.data.errorMessage)
    })
}
export {
    viewShow,
    bookShow
}
