let movieInitialState = {
    movies : [],
    movie : ''
}

const movieReducer = ( state = movieInitialState, action ) => {
    switch(action.type){
        case 'SET_MOVIES' : return {
            ...state,
            movies : action.payload
        }
        case 'SET_MOVIE' :  return {
            ...state,
            movie : action.payload
        }
        default : return state
    }
}

export{
    movieReducer
}
