import axios from "axios"
import { SET_SHOW } from "./actionTypes"
const setShow = (dates) => {
    return {
        type : SET_SHOW,
        payload : dates
    }
}
const setSeats = (seats) => {
    return {
        type : 'SET_SEATS',
        payload : seats
    }
}
const viewShow = (movieId) => {
    return(dispatch) => {
        axios.get(`http://localhost:3040/shows/${movieId}`)
        .then((show) => {
            console.log("shows sheduled : ",show.data)
            dispatch(setShow(show.data))
        })
        .catch((err) => console.log(err))
    }
}

const viewSeats = (movieId, showDate) => {
    return(dispatch) => {
        axios.get(`http://localhost:3040/shows/${movieId}/${showDate}`)
        .then( seats => {
            console.log("seats : ",seats.data.showSeats)
            dispatch(setSeats(seats.data.show))
        })
        .catch(err =>  console.log("err : ",err))
    }
}
export {
    viewShow,
    viewSeats
}
