import { Box, CardMedia, Paper, SnackbarContent, Stack, Typography } from "@mui/material";
import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { viewMovie } from "../redux/movieActions";

const AboutMovie = () => {
    let { movieId } = useParams();
    const dispatch = useDispatch()
    const movieDetails = useSelector(state => state.movie.film)
    const { movieName, description, actorName, directorName, startBookingDate } = movieDetails
    console.log("details : ", movieDetails)
    console.log("movieId : ", movieId)
    useEffect(() => {
        dispatch(viewMovie(movieId))
    }, [movieId])
    return (<>
        <iframe src={movieDetails.movieVideoUrl} height={200} width={300} style={{ marginLeft: '580px', marginTop: '100px' }} allow='autoplay'></iframe>
        <Box sx={{
            width: 400,
            height: 240,
            margin: 'auto',
            backgroundColor: '#756f6e'
        }}>
            <Box sx={{ padding: '40px' }}>
                <Typography>Movie Name - {movieName}</Typography>
                <Typography>Actor Name - {actorName}</Typography>
                <Typography>Director Name - {directorName}</Typography>
                <Typography>Description - {description}</Typography>
                <Typography>Release Date - {startBookingDate}</Typography>
                <Typography>Ticket Cost - {movieName}</Typography>
            </Box>
        </Box>
        <Stack spacing={2} sx={{ maxWidth: 600 }}>
            <SnackbarContent message="I love snacks." action="Movie Name" />
            <SnackbarContent
                message={
                    'I love candy. I love cookies. I love cupcakes. \
          I love cheesecake. I love chocolate.'
                }
            />
            <SnackbarContent
                message="I love candy. I love cookies. I love cupcakes."
                action=""
            />
            <SnackbarContent
                message={
                    'I love candy. I love cookies. I love cupcakes. \
          I love cheesecake. I love chocolate.'
                }
            />
        </Stack>

    </>)
}

export default AboutMovie
