import { SET_LOGIN, SET_LOGOUT, SET_SIGNUP, SET_SIGNOUT, TOGGLE_SIGNUP, SET_ADMIN_LOGIN, SET_ADMIN_LOGOUT, SIGNUP } from "./authTypes"
import axios from 'axios'

const setLogin = () => {
    return {
        type : SET_LOGIN
    }   
}
const setLogout = () => {
    return {
       type : SET_LOGOUT
    }  
}
const setSignUp = () => {
    return {
        type : SET_SIGNUP
    }
}
const setSignOut = () => {
    return {
        type : SET_SIGNOUT
    }
}
const toggleSignup = () => {
    return {
        type : TOGGLE_SIGNUP
    }
}
const setAdminLogin = () => {
    return {
        type : SET_ADMIN_LOGIN
    }
} 
const setAdminLogout = () => {
    return {
        type : SET_ADMIN_LOGOUT
    }
} 
const signUp = (token) => {
    return {
        type : SIGNUP,
        token : token.data
    }
}

const storeUserToken = (user, type = 'login') => {
    return(dispatch) => {
        axios.post(`http://localhost:3040/users/${type}`,user)
        .then( token => {
            if(type === 'login')
            {
            localStorage.setItem("usersToken", token.data.accessToken)
            dispatch(signUp(token))
            }
        })
        .catch( err => console.log(err) )
    }
}

export {
    setLogin,
    setLogout,
    setSignUp,
    setSignOut,
    toggleSignup,
    setAdminLogin,
    setAdminLogout,
    signUp,
    storeUserToken
}
